--CREAR BASE DE DATOS--CREATE DATABASE Clase7;--CREAR TABLA ESTADOS--CREATE TABLE Estados(IDEstado int NOT NULL IDENTITY PRIMARY KEY, --IDENTITY SE GENERA id AUTOINCREMENTAL--Nombre varchar(50) NOT NULL);--INSERTAR DATOS EN TABLA ESTADOSS--INSERT INTO EstadosVALUES  ('Ingresado en Planta'),('En Camino'),('Espera en sucursal'),('Entregado');--VISUALIZAR TABLA--SELECT * FROM Estados;--CREAR TABLA DE ENVIOS --CREATE TABLE Envios(IDEnvio varchar(20) NOT NULL PRIMARY KEY,IDEstado int FOREIGN KEY REFERENCES Estados(IDEstado), Descripcion varchar(50) NOT NULL,FechaEnvio datetime NOT NULL);INSERT INTO Envios (IDEnvio, IDEstado,Descripcion, FechaEnvio)values ('EN-283450-AR',1,'Paquete fragil','02-25-2021'),('EX-394899-CO',1,' No colocarlo en lugares humedos', '02-25-2021'),('EN-485970-CL',2,' Presenta golpes','04-21-2021'),('EX-298895-UY',3,' Caja Vacia tiene poco peso', '05-17-2021'),('EX-308498-MX',4,' Presenta abolladura', GETDATE());--Sistema español primero día / después mes--INSERT INTO Envios (IDEnvio, IDEstado,Descripcion, FechaEnvio)values ('EN-283450-AR',1,'Paquete fragil','25-02-2021'),('EX-394899-CO',1,' No colocarlo en lugares humedos', '25-02-2021'),('EN-485970-CL',2,' Presenta golpes','21-04-2021'),('EX-298895-UY',3,' Caja Vacia tiene poco peso', '17-05-2021'),('EX-308498-MX',4,' Presenta abolladura', GETDATE());SELECT * FROM EnviosWHERE LEFT(IDEnvio,2)='EN' 
SELECT * FROM EnviosWHERE LEFT(IDEnvio,2) = 'EX';SELECTCONCAT(IDEnvio,IDEstado) AS NuevoIDFROM Envios
SELECTCONCAT(IDEnvio,'-',IDEstado) AS NuevoIDFROM EnviosSELECTCONCAT (LEFT (IDEnvio,2),'-',RIGHT (IDEnvio,2)) AS NewCodeFROM Envios;SELECTREPLACE(IDEnvio,'-','') AS NuevoIDFROM EnviosSELECTUPPER(Descripcion) AS MAYUSCFROM EnviosSELECTCONCAT(LEFT(IDEnvio,2),'-',RIGHT(IDEnvio,2)) AS NuevoID,IDEnvio,DescripcionFROM EnviosSELECT * FROM EnviosSELECTREPLACE(IDEnvio,'-','') AS NuevoIDFROM EnviosSELECT IDEnvio,IDEstado,UPPER (Descripcion) AS MAYUSC,FechaEnvioFROM EnviosSELECTUPPER(Descripcion) AS MAYUSCFROM EnviosSELECTlower(Descripcion) AS MINUSCFROM EnviosSELECT IDEnvio,IDEstado,TRIM (Descripcion) AS MAYUSC,FechaEnvioFROM Envios-- para extraer ciertos caracteres dentro de un grupo de caracteres fijos--selectSUBSTRING(IDEnvio,4,6)IDEstado,Descripcion,FechaEnviofrom EnviosSELECTFechaEnvio,DAY(FechaEnvio) AS Dia,MONTH(FechaEnvio) AS Mes,YEAR(FechaEnvio) AS AñoFROM EnviosSELECTFechaEnvio,DAY(FechaEnvio) AS Dia,DATENAME(WEEKDAY,FechaEnvio) AS Nombremes,MONTH(FechaEnvio) AS Mes,YEAR(FechaEnvio) AS AñoFROM EnviosSELECT IDEnvio, FechaEnvio,DATEADD(MONTH,1,FechaEnvio) AS Proyeccion_MesFROM Envios