CREATE DATABASE Clase4

CREATE TABLE Clientes (IDCliente int NOT NULL IDENTITY PRIMARY KEY,NombreCliente varchar(50) NOT NULL,Direccion varchar (50) NOT NULL,Ciudad varchar (50) NOT NULL,CP  varchar (50) NOT NULL,Pais text NOT NULL);CREATE TABLE Empleados (IDEmpleados int NOT NULL IDENTITY PRIMARY KEY,Apellido varchar (50) NOT NULL,Nombre varchar (50) NOT NULL,Nacimiento datetime NOT NULL,Notas varchar (50) NOT NULL);CREATE TABLE Ordenes (IDOrden int NOT NULL IDENTITY PRIMARY KEY, FechaOrden datetime NOT NULL,IDCliente int NOT NULL FOREIGN KEY REFERENCES Clientes (IDCliente),IDEmpleados int NOT NULL FOREIGN KEY REFERENCES Empleados (IDEmpleados));SELECT * FROM OrdenesALTER TABLE OrdenesADD Cantidad int;ALTER TABLE OrdenesALTER COLUMN Cantidad varchar (10);DROP TABLE Ordenes